import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import './Home.css';
import Footer from './footer';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Carousel } from 'react-bootstrap';
import image1 from '../images/image1.jpeg';
import image2 from '../images/image2.jpg';
import image3 from '../images/image3.jpeg';
import image4 from '../images/image4.jpeg';
import image5 from '../images/image5.jpeg';






function Home() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleSelect = (selectedIndex) => {
    setCurrentImageIndex(selectedIndex);
  };

  return (
        <div className="home-container">
        <Carousel activeIndex={currentImageIndex} onSelect={handleSelect} controls={false} indicators={false} fade>
          <Carousel.Item>
            <img className="d-block w-100" src={image1} alt="Image 1" />
          </Carousel.Item>
          <Carousel.Item>
            <img className="d-block w-100" src={image2} alt="Image 2" />
          </Carousel.Item>
          <Carousel.Item>
            <img className="d-block w-100" src={image3} alt="Image 3" />
          </Carousel.Item>
          <Carousel.Item>
            <img className="d-block w-100" src={image4} alt="Image 4" />
          </Carousel.Item>
          <Carousel.Item>
            <img className="d-block w-100" src={image5} alt="Image 5" />
          </Carousel.Item>
        </Carousel>
      <Navbar className="navvv" />


      

      


      <Footer />

    </div>
  );
}

export default Home;
