import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import './Home.css';
import Footer from './footer';

const backgroundImages = [
  'bg-image1',
  'bg-image2',
  'bg-image3',
  'bg-image4',
  'bg-image5',
  'bg-image6',
  'bg-image7',
  'bg-image8',
  'bg-image9',
  'bg-image10',
  'bg-image11',
  'bg-image12'
];

function Home() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex === backgroundImages.length - 1 ? 0 : prevIndex + 1));
    }, 4000); // Change image every 4 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home-container">
      <div className="image-container">
        {backgroundImages.map((image, index) => (
          <div
            key={index}
            className={`background-image ${image} ${index === currentImageIndex ? 'active' : ''}`}
          />
        ))}
      </div>
      <Navbar className="navvv" />
      <div className="card-container">
        <div className="card">
          <h3 className="card-title">University of California, Berkeley</h3>
          <p className="card-description">
            Founded in 1868, UC Berkeley is a public research university located in Berkeley, California. It is considered one of the world's leading universities for its academic excellence and research contributions.
          </p>
        </div>
        <div className="card">
          <h3 className="card-title">Harvard University</h3>
          <p className="card-description">
            Established in 1636, Harvard is a private Ivy League research university located in Cambridge, Massachusetts. It is one of the most prestigious and renowned universities in the world, known for its academic rigor and influential alumni.
          </p>
        </div>
        <div className="card">
          <h3 className="card-title">Massachusetts Institute of Technology (MIT)</h3>
          <p className="card-description">
            Founded in 1861, MIT is a private research university located in Cambridge, Massachusetts. It is widely regarded as one of the world's leading universities, particularly in the fields of science, engineering, and technology.
          </p>
        </div>
        <div className="card">
          <h3 className="card-title">University of Oxford</h3>
          <p className="card-description">
            Established in the 11th century, the University of Oxford is a collegiate research university located in Oxford, England. It is one of the oldest and most prestigious universities in the world, renowned for its academic excellence and rich history.
          </p>
        </div>
        <div className="card">
          <h3 className="card-title">Stanford University</h3>
          <p className="card-description">
            Founded in 1885, Stanford is a private research university located in Stanford, California. It is consistently ranked among the top universities in the world and is known for its entrepreneurial spirit and contributions to various fields, including technology, engineering, and medicine.
          </p>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Home;
