import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import Navbar from './Navbar';

function CustomCalendar() {
  const [date, setDate] = useState(new Date());
  const [events, setEvents] = useState([
    { date: new Date(2023, 4, 10), title: 'Birthday Party', color: '#ffa07a' },
    { date: new Date(2023, 4, 15), title: 'Team Meeting', color: '#87cefa' },
    { date: new Date(2023, 4, 20), title: 'Vacation', color: '#98fb98' },
    { date: new Date(2024, 5, 10), title: 'Birthday', color: '#ff69b4' },
    { date: new Date(2024, 5, 15), title: 'Festival', color: '#ffd700' },
  ]);

  const tileContent = ({ date, view }) => {
    const eventForDate = events.find(
      (event) =>
        event.date.getFullYear() === date.getFullYear() &&
        event.date.getMonth() === date.getMonth() &&
        event.date.getDate() === date.getDate()
    );
    return eventForDate ? (
      <div
        style={{
          backgroundColor: eventForDate.color,
          color: '#fff',
          padding: '5px',
          borderRadius: '5px',
        }}
      />
    ) : null;
  };

  const getEventsForMonth = (month, year) => {
    const eventsForMonth = events.filter(
      (event) =>
        event.date.getMonth() === month && event.date.getFullYear() === year
    );
    return eventsForMonth.length > 0 ? eventsForMonth : null;
  };

  const calendarContainerStyle = {
    width: '100%',
    height: '100%',
    border: 'none',
    borderRadius: '0',
    padding: '0',
    backgroundColor: '#fff',
    boxShadow: 'none',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    margin: '0',
  };

  const pageStyle = {
    backgroundColor: '#000',
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  };

  return (
    <div style={pageStyle}>
      <Navbar />
      <div style={calendarContainerStyle}>
        <div style={{ display: 'flex' }}>
          <Calendar
            onChange={setDate}
            value={date}
            tileContent={tileContent}
            tileDisabled={() => false}
            tileClassName={({ date }) => {
              const isWeekend = date.getDay() === 0 || date.getDay() === 6;
              return isWeekend ? 'weekend' : null;
            }}
            navigationLabel={({ date }) =>
              `${date.toLocaleString('default', { month: 'long' })} ${date.getFullYear()}`
            }
            prev2Label={null}
            next2Label={null}
            prevLabel={<span style={{ fontSize: '1.5rem' }}>&lt;</span>}
            nextLabel={<span style={{ fontSize: '1.5rem' }}>&gt;</span>}
            showNeighboringMonth={false}
          />
          <div style={{ marginLeft: '20px' }}>
            <h2>Events for {date.toLocaleString('default', { month: 'long' })} {date.getFullYear()}</h2>
            {getEventsForMonth(date.getMonth(), date.getFullYear()) ? (
              getEventsForMonth(date.getMonth(), date.getFullYear()).map((event, index) => (
                <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                  <div
                    style={{
                      width: '20px',
                      height: '20px',
                      backgroundColor: event.color,
                      marginRight: '10px',
                    }}
                  />
                  <span>{event.title}</span>
                </div>
              ))
            ) : (
              <p>No events for this month</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default CustomCalendar;
