import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Calendar from './Calendar';
import './admin.css';

const AdminPage = () => {
  const [notifications, setNotifications] = useState([]);
  const [users, setUsers] = useState([]);
  const [newNotification, setNewNotification] = useState({ data: '', from: '' });

  useEffect(() => {
    fetchNotifications();
    fetchUsers();
  }, []);

  const fetchNotifications = async () => {
    try {
      const response = await axios.get('http://localhost:4000/notifications');
      setNotifications(response.data);
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:4000/login');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleNotificationChange = (e) => {
    setNewNotification({ ...newNotification, [e.target.name]: e.target.value });
  };

  const handleNotificationSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:4000/notifications', newNotification);
      setNewNotification({ data: '', from: '' });
      fetchNotifications();
    } catch (error) {
      console.error('Error creating notification:', error);
    }
  };

  const handleUserDelete = async (userId) => {
    try {
      await axios.delete(`http://localhost:4000/login/${userId}`);
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="admin-content">
        <div className="section">
          <h2>Notifications</h2>
          <form onSubmit={handleNotificationSubmit}>
            <input
              type="text"
              name="data"
              placeholder="Notification Data"
              value={newNotification.data}
              onChange={handleNotificationChange}
            />
            <input
              type="text"
              name="from"
              placeholder="From"
              value={newNotification.from}
              onChange={handleNotificationChange}
            />
            <button type="submit">Add Notification</button>
          </form>
          <ul>
            {notifications.map((notification) => (
              <li key={notification._id}>
                <span>{notification.data}</span>
                <span>{notification.from}</span>
                <button onClick={() => handleNotificationDelete(notification._id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
        <div className="section">
          <h2>Calendar</h2>
          <Calendar />
        </div>
        <div className="section">
          <h2>User Management</h2>
          <ul>
            {users.map((user) => (
              <li key={user._id}>
                <span>{user.email}</span>
                <span>{user.college}</span>
                <button onClick={() => handleUserDelete(user._id)}>Delete</button>
                <button>Update</button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
