import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import Navbar from './Navbar';

function CustomCalendar() {
  const [date, setDate] = useState(new Date());
  const [events, setEvents] = useState([
    { date: new Date(2023, 4, 10), title: 'Birthday Party', color: '#ffa07a' },
    { date: new Date(2023, 4, 15), title: 'Team Meeting', color: '#87cefa' },
    { date: new Date(2023, 4, 20), title: 'Vacation', color: '#98fb98' },
  ]);

  const tileContent = ({ date, view }) => {
    const eventForDate = events.find(
      (event) =>
        event.date.getFullYear() === date.getFullYear() &&
        event.date.getMonth() === date.getMonth() &&
        event.date.getDate() === date.getDate()
    );

    return eventForDate ? (
      <div
        style={{
          backgroundColor: eventForDate.color,
          color: '#fff',
          padding: '5px',
          borderRadius: '5px',
        }}
      >
        {eventForDate.title}
      </div>
    ) : null;
  };



  const calendarContainerStyle = {
    width: '100%', // Take up the full width of the page
    height: '100%', // Take up the full height of the page
    border: 'none', // Remove the border
    borderRadius: '0', // Remove the border radius
    padding: '0', // Remove the padding
    backgroundColor: '#fff',
    boxShadow: 'none', // Remove the box shadow
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    margin: '0', // Remove the margin
  };

  
  const pageStyle = {
    backgroundColor: '#000', // Set the background color of the page to black
    height: '100vh', // Set the height of the page to fill the viewport
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  };

  return (
    <div style={pageStyle}>
        <Navbar />
      <div style={calendarContainerStyle}>
        <Calendar
          onChange={setDate}
          value={date}
          tileContent={tileContent}
          tileDisabled={() => false}
          tileClassName={({ date }) => {
            const isWeekend = date.getDay() === 0 || date.getDay() === 6;
            return isWeekend ? 'weekend' : null;
          }}
          navigationLabel={({ date }) => `${date.toLocaleString('default', { month: 'long' })} ${date.getFullYear()}`}
          prev2Label={null}
          next2Label={null}
          prevLabel={<span style={{ fontSize: '1.5rem' }}>&lt;</span>}
          nextLabel={<span style={{ fontSize: '1.5rem' }}>&gt;</span>}
          showNeighboringMonth={false}
        />
      </div>
    </div>
  );
}

export default CustomCalendar;
