import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import './Home.css';
import Footer from './footer';

const backgroundImages = [
  'bg-image1',
  'bg-image2',
  'bg-image3',
  'bg-image4',
  'bg-image5',
  'bg-image6',
  'bg-image7',
  'bg-image8',
  'bg-image9',
  'bg-image10',
  'bg-image11',
  'bg-image12'
];

function Home() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) =>
        prevIndex === backgroundImages.length - 1 ? 0 : prevIndex + 1
      );
    }, 4000); // Change image every 4 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home-container">
      {backgroundImages.map((image, index) => (
        <div
          key={index}
          className={`background-image ${image} ${index === currentImageIndex ? 'active' : ''}`}
        />
      ))}
      <Navbar className="navvv" />

      <div class="container">
  <div class="header">
    <h1>Move faster with your tools in one place</h1>
    <p>Automate away routine tasks with the power of generative AI and simplify your workflow with all your favourite apps ready to go in Slack.</p>
    <a href="https://www.google.com">Learn more about the Slack platform</a>
  </div>
  <div class="section">
    <div class="left">
      <h2>Choose how you want to work</h2>
      <p>In Slack, you've got all the flexibility to work when, where and how it's best for you. You can easily chat, send audio and video clips, or join a huddle to talk things through live.</p>
      <a href="https://www.google.com">Learn more about flexible communication</a>
    </div>
    <div class="right">
    </div>
  </div>
  <div class="section">
    <div class="left">
    </div>
    <div class="right">
      <h2>Bring your team together</h2>
      <p>At the heart of Slack are channels: organized spaces for everyone and everything that you need for work. In channels, it's easier to connect across departments, offices, time zones and even other companies.</p>
      <a href="#">Learn more about channels</a>
    </div>
  </div>
</div>


      <Footer />

    </div>
  );
}

export default Home;
